/** * A class that represents a comparable object that can be in a priority queue. *  * @author Frank M. Carrano * @version 3.0 */public class Thing<T> implements Comparable<Thing<T>> {		private T data;	private int priority;	public Thing(T dataValue, int priorityValue) {		data = dataValue;		priority = priorityValue;	}	public T getData() {		return data;	}	public int getPriority() {		return priority;	}	public void setData(T newData) {		data = newData;	}	public void setPriority(int newPriority) {		priority = newPriority;	}	@Override	public int compareTo(Thing<T> other) {		int result;		if (priority == other.priority)			result = 0;		else if (priority < other.priority)			result = -1;		else			result = 1;		return result;	}	@Override	public String toString() {		return data.toString() + " " + priority;	}}